/*
Dynamic data masking example for Azure Synapse SQL Dedicated
*/

-- in ddmtesting2 database

-- create schema
create schema ddm;


/*
drop table dbo.securedata;
*/

-- create table
create table ddm.securedata(
     [MemberID]         int not null
    ,[FirstName]        varchar(100) not null
    ,[LastName]         varchar(100) not null
    ,[PostalAddress]    varchar(100) not null
    ,[CreditCard]       decimal(16,0) not null
)
with
(
	 distribution = round_robin
    ,clustered columnstore index
);


-- insert some data
insert into ddm.securedata values (1234,'Francisque','Thandeka','100 High Street',1111222233334444);
insert into ddm.securedata values (1235,'Vanda','Maeleachlainn','101 High Street',2222333344445555);
insert into ddm.securedata values (1236,'Josefina','Visnja','102 High Street',3333444455556666);
insert into ddm.securedata values (1237,'Andrs','Winfrith','103 High Street',4444555566667777);
insert into ddm.securedata values (1239,'Josephus','Panos','105 High Street',6666777788889999);

-- View data in table
select * from ddm.securedata;
-- All data visible to everyone who has access to the table.
/*
MemberID    FirstName   LastName        PostalAddress       CreditCard
1234	    Francisque	Thandeka	    100 High Street	    1111222233334444
1237	    Andrs	    Winfrith	    103 High Street	    4444555566667777
1236	    Josefina	Visnja	        102 High Street	    3333444455556666
1239	    Josephus	Panos	        105 High Street	    6666777788889999
1235	    Vanda	    Maeleachlainn	101 High Street	    2222333344445555
*/


-- Create user accounts
create user User1 without login;
create user User2 without login;
create user User3 without login;

-- Give users access to run select against the objects in schema ddm
grant select on schema::ddm to User1
grant select on schema::ddm to User2
grant select on schema::ddm to User3


-- add masking to the table
alter table ddm.securedata alter column PostalAddress add masked with (function = 'default()');
alter table ddm.securedata alter column CreditCard add masked with (function = 'random(1, 100)');

-- remove masking from the table - only run this for testing, don't remove masking when running test expecting to see masked data
/*
alter table dbo.securedata alter column PostalAddress drop masked;
alter table dbo.securedata alter column CreditCard drop masked;
*/

-- Check the data:
select * from ddm.securedata;
-- Data is not masked for me (ored@microsoft.com, AAD Admin)


-- What User1 would see
execute as user = 'User1';  
select * from ddm.securedata;
revert;
-- both columns are masked

-- What User2 would see
execute as user = 'User2';  
select * from ddm.securedata;
revert;
-- both columns are masked

-- What User3 would see
execute as user = 'User3';  
select * from ddm.securedata;
revert;
-- both columns are masked

/*
-- Customer has requirement that 
	User1 be able to see [PostalAddress]
	User2 be able to see [CreditCard]
	User3 be able to see [PostalAddress] & [CreditCard]
*/
-- Allow User3 to see the masked data, User3 can now see [PostalAddress] & [CreditCard] unmasked
grant unmask on ddm.securedata(PostalAddress) to User1;
grant unmask on ddm.securedata(CreditCard) to User2;
grant unmask to User3;


-- What User1 would see
execute as user = 'User1';  
select * from ddm.securedata;
revert;
-- both columns are masked (not as expected)
-- Expected: PostalAddress to be unmasked

-- What User2 would see
execute as user = 'User2';  
select * from ddm.securedata;
revert;
-- both columns are masked (not as expected)
-- Expected: CreditCard to be unmasked

-- What User3 would see
execute as user = 'User3';  
select * from ddm.securedata;
revert;
-- both columns are unmasked (as expected)

// Drop target table for IoT data:
