// Create target table for IoT data:
.create table buidingenv (timestamp: datetime, temperature: real, humidity: real)
.create table buidingenv ingestion json mapping 'buidingenvMapping' '[{"column": "timestamp","path": "$.timestamp","datatype": "datetime" },{"column": "humidity","path": "$.humidity","datatype": "real"},{"column": "temperature","path": "$.temperature","datatype": "real"}]'

// Return all records
buidingenv_v2 | count


// Return all records for the last day
buidingenv_v2
//| where timestamp > now(-1d) and timestamp <= now()
| order by timestamp desc
| limit 10
| count


// Average temperature by date & hour, rounded to 2 decimal places
buidingenv 
| summarize round(avg(temperature),2) by 
     year = datetime_part("year", timestamp)
    ,month = datetime_part("month", timestamp)
    ,day = datetime_part("day", timestamp)
    ,hour = datetime_part("hour", timestamp)
| where hour != ''
| order by year asc, month asc, day asc, hour asc

// Count of records per hour
buidingenv 
| summarize RecordCount = count() by ISO8601 = substring(tostring(timestamp),0,13)
| where ISO8601 != ''
| order by ISO8601 asc


// Now switch to the new table

// Count of messages per deviceID
buidingenv_v2
| summarize RecordCount = count() by device = deviceid
| order by device asc
// IoTUbuntu           3405
// IoTWinServer2019    2493

