use [biketrips];
use [iot];


drop USER palantiri
GO

CREATE USER palantiri FROM EXTERNAL PROVIDER
GO

CREATE USER palantiri FOR LOGIN palantiri;
GO

ALTER ROLE db_datareader ADD MEMBER palantiri;
GO


GRANT REFERENCES ON DATABASE SCOPED CREDENTIAL::[WorkspaceIdentity] TO [palantiri];


select * from sys.database_scoped_credentials



